DROP TABLE Jungle;

CREATE TABLE Jungle(
	id INT IDENTITY(1, 1) PRIMARY KEY,
	cardName VARCHAR(255) NOT NULL,
	setName VARCHAR(255) NOT NULL,
	setNumber VARCHAR(10) NOT NULL,
	PokemonType VARCHAR(20) NOT NULL,
	cardType VARCHAR(20) NOT NULL,
	other VARCHAR(255),
	photo VARCHAR(255)
);


